    if ( this.viewport ) {
        var currRotation = this.viewport.getRotation();

        if ( this.viewport.flipped ){
          currRotation = $.positiveModulo(currRotation - 90, 360);
        } else {
          currRotation = $.positiveModulo(currRotation + 90, 360);
        }
        this.viewport.setRotation(currRotation);
    }
